import React from "react";
import "./App.css";
import { Card, Icon } from "antd";
import Nav from "./Nav";
import { useParams } from "react-router-dom";
import { useState } from "react";
import { useEffect } from "react";
const { Meta } = Card;

function ScreenArticlesBySource() {
  var { id } = useParams();

  console.log("ID :", typeof id);

  const [articleList, setArticleList] = useState([]);

  var req =
    "https://newsapi.org/v2/top-headlines?apiKey=dd0594311c8644e7a83119ce2dcdd00b&language=fr&sources=" +
    id;

  console.log("REQUEST : ", req);

  useEffect(() => {
    fetch(req)
      .then((response) => response.json())
      .then((data) =>
        setArticleList(
          data.articles.map((element) => (
            <div className="Card">
              <div
                style={{ display: "flex", justifyContent: "center" }}
                span={8}
              >
                <Card
                  style={{
                    width: 300,
                    margin: "15px",
                    display: "flex",
                    flexDirection: "column",
                    justifyContent: "space-between",
                  }}
                  cover={<img alt="example" src={element.urlToImage} />}
                  actions={[
                    <Icon type="read" key="ellipsis2" />,
                    <Icon type="like" key="ellipsis" />,
                  ]}
                >
                  <Meta
                    title={element.title}
                    description={element.description}
                  />
                </Card>
              </div>
            </div>
          ))
        )
      );
  }, []);

  console.log("ID : ", id);

  return (
    <div>
      <Nav />

      <div className="Banner" />

      <div className="site-card-wrapper">{articleList}</div>
    </div>
  );
}

export default ScreenArticlesBySource;
