import React from "react";
import "./App.css";
import { Card, Icon, Row, Col, Modal, Button } from "antd";
import Nav from "./Nav";
import { useParams } from "react-router-dom";
import { useState } from "react";
import { useEffect } from "react";
import { connect } from "react-redux";
const { Meta } = Card;

function ScreenArticlesBySource(props) {
  var { id } = useParams();

  const [articleArray, setArticleArray] = useState([]);
  const [modalTitle, setModalTitle] = useState("");
  const [modalContent, setModalContent] = useState("");
  const [sourceLanguage, setSourceLanguage] = useState("fr");

  const [isModalVisible, setIsModalVisible] = useState(false);

  const showModal = (i) => {
    setModalTitle(articleArray[i].title);
    setModalContent(articleArray[i].content);
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  function changeLangue(langue) {
    console.log(
      "ðŸš€ ~ file: ScreenArticlesBySource.js ~ line 36 ~ ScreenArticlesBySource ~ langue",
      langue
    );

    setSourceLanguage(langue);
  }

  useEffect(() => {
    var req =
      "https://newsapi.org/v2/top-headlines?apiKey=dd0594311c8644e7a83119ce2dcdd00b&language=" +
      sourceLanguage +
      "&sources=" +
      id;
    fetch(req)
      .then((response) => response.json())
      .then((data) => {
        setArticleArray(data.articles);
      });
  }, []);

  useEffect(() => {
    var req =
      "https://newsapi.org/v2/top-headlines?apiKey=dd0594311c8644e7a83119ce2dcdd00b&language=" +
      sourceLanguage +
      "&sources=" +
      id;
    fetch(req)
      .then((response) => response.json())
      .then((data) => {
        setArticleArray(data.articles);
      });
  }, [sourceLanguage]);

  return (
    <div>
      <Nav />
      <div className="Banner">
        <Button onClick={() => changeLangue("fr")}>French</Button>
        <Button onClick={() => changeLangue("en")}>English</Button>
      </div>
      <div className="site-card-wrapper" style={{ display: "flex" }}>
        <Row gutter={16}>
          {articleArray.map((element, i) => (
            <Col span={8}>
              <Card
                style={{
                  height: 500,
                  width: 300,
                  display: "flex",
                  flexDirection: "column",
                }}
                cover={<img alt="example" src={element.urlToImage} />}
                actions={[
                  <Icon
                    type="read"
                    key="ellipsis2"
                    onClick={() => showModal(i)}
                  />,
                  <Icon
                    type="like"
                    key="ellipsis"
                    onClick={() =>
                      props.addArticle({
                        title: element.title,
                        description: element.description,
                        content: element.content,
                        img: element.urlToImage,
                      })
                    }
                  />,
                ]}
              >
                <Meta title={element.title} description={element.description} />
              </Card>
            </Col>
          ))}
        </Row>
      </div>
      if(modalArticle)
      {
        <Modal
          title={modalTitle}
          visible={isModalVisible}
          onOk={handleOk}
          onCancel={handleCancel}
        >
          <p>{modalContent}</p>
        </Modal>
      }
    </div>
  );
}

function mapDispatchToProps(dispatch) {
  return {
    addArticle: function (article) {
      dispatch({ type: "addArticle", article });
    },
  };
}

export default connect(null, mapDispatchToProps)(ScreenArticlesBySource);
