import React from "react";
import "./App.css";
import { Card, Icon, Row, Col, Button, Modal } from "antd";
import Nav from "./Nav";
import { useParams } from "react-router-dom";
import { useState } from "react";
import { useEffect } from "react";
const { Meta } = Card;

function ScreenArticlesBySource() {
  var { id } = useParams();

  console.log("ID :", typeof id);

  const [articleList, setArticleList] = useState([]);

  const [isModalVisible, setIsModalVisible] = useState(false);

  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  var req =
    "https://newsapi.org/v2/top-headlines?apiKey=dd0594311c8644e7a83119ce2dcdd00b&language=fr&sources=" +
    id;

  console.log("REQUEST : ", req);

  useEffect(() => {
    fetch(req)
      .then((response) => response.json())
      .then((data) =>
        setArticleList(
          data.articles.map((element) => (
            <Col span={8}>
              <Card
                cover={<img alt="example" src={element.urlToImage} />}
                actions={[
                  <Icon type="read" key="ellipsis2" />,
                  <Icon type="like" key="ellipsis" />,
                ]}
              >
                <Meta title={element.title} description={element.description} />
              </Card>
            </Col>
          ))
        )
      );
  }, []);

  console.log("ID : ", id);

  return (
    <div>
      <Nav />

      <div className="Banner" />

      <div className="site-card-wrapper">
        <Row gutter={16}>{articleList}</Row>
      </div>
    </div>
  );
}

export default ScreenArticlesBySource;
