import React from 'react';
import './App.css';
import { Card, Icon} from 'antd';
import Nav from './Nav'
import { useParams } from 'react-router-dom';
import { useState } from 'react';
import { useEffect } from 'react';
const { Meta } = Card;


function ScreenArticlesBySource() {

  var {id} = useParams();

  const [articleList, setArticleList] = useState([]); 

  useEffect(() => {

    fetch('https://newsapi.org/v2/top-headlines/sources?apiKey=dd0594311c8644e7a83119ce2dcdd00b&language=fr&country=fr')
    .then(response => response.json())
    .then(data => setArticleList(data.sources.map(element => 
      (

        <Card
        style={{ 
        width: 300, 
        margin:'15px', 
        display:'flex',
        flexDirection: 'column',
        justifyContent:'space-between' }}
        cover={
        <img
            alt="example"
            src= {element.urlToImage}
        />
        }
        actions={[
            <Icon type="read" key="ellipsis2" />,
            <Icon type="like" key="ellipsis"/>
        ]}
        >

        <Meta
          title={element.title}
          description={element.description}
        />

      </Card>
      ))))

  }, [])

  console.log('ID : ', id); 

  return (
    <div>

            <Nav/>

            <div className="Banner"/>

            <div className="Card">

              <div  style={{display:'flex',justifyContent:'center'}}>

                {articleList}

              </div>




           </div> 



      </div>
  );
}

export default ScreenArticlesBySource;
