export default function (articleList = [], action) {
  console.log("ðŸš€ ~ file: article.reducer.js ~ line 2 ~ action", action);
  if (action.type === "addArticle") {
    var tmpArticles = [...articleList];
    tmpArticles.push(action.article);
    console.log(
      "ðŸš€ ~ file: article.reducer.js ~ line 8 ~ tmpArticles",
      tmpArticles
    );
    if (action.type === "removeArticle") {
      var tmpArticles = [...articleList];
      tmpArticles.splice(
        tmpArticles.findIndex(
          (element) => element.title === action.articleTitle
        ),
        1
      );
      return tmpArticles;
    }
    return tmpArticles;
  }
  return articleList;
}
