import React from "react";
import "./App.css";
import { Card, Col, Icon, Row } from "antd";
import Nav from "./Nav";
import { connect } from "react-redux";
import { useState } from "react";

const { Meta } = Card;

function ScreenMyArticles(props) {
  const [modalTitle, setModalTitle] = useState("");
  const [modalContent, setModalContent] = useState("");
  const [sourceLanguage, setSourceLanguage] = useState("fr");
  const [isModalVisible, setIsModalVisible] = useState(false);

  const showModal = (i) => {
    setModalTitle(articleArray[i].title);
    setModalContent(articleArray[i].content);
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  var noArticles;
  if (props.articleList.length === 0) {
    noArticles = "No articles";
  }
  return (
    <div>
      <Nav />
      <div className="Banner" />
      {noArticles}
      <div style={{ display: "flex", justifyContent: "center" }}>
        <Row gutter={16}>
          {props.articleList.map((element, i) => (
            <Col span={8}>
              <Card
                style={{
                  width: 300,
                  margin: "15px",
                  display: "flex",
                  flexDirection: "column",
                  justifyContent: "space-between",
                }}
                cover={<img alt="example" src={element.img} />}
                actions={[
                  <Icon
                    type="read"
                    key="ellipsis2"
                    onClick={() => showModal(i)}
                  />,
                  <Icon
                    type="delete"
                    key="ellipsis"
                    onClick={() => props.removeArticle(element.title)}
                  />,
                ]}
              >
                <Meta title={element.title} description={element.description} />
              </Card>
            </Col>
          ))}
        </Row>
      </div>
      <Modal
        title={modalTitle}
        visible={isModalVisible}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <p>{modalContent}</p>
      </Modal>
    </div>
  );
}

function mapStateToProps(state) {
  return { articleList: state.articleList };
}

function mapDispatchToProps(dispatch) {
  return {
    removeArticle: function (articleTitle) {
      dispatch({ type: "removeArticle", articleTitle });
    },
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(ScreenMyArticles);
