import React, { useState, useEffect } from "react";
import "./App.css";
import { List, Avatar } from "antd";
import Nav from "./Nav";
import { Link } from "react-router-dom";

function ScreenSource() {
  const [sourceList, setSourceList] = useState([]);

  useEffect(() => {
    fetch(
      "https://newsapi.org/v2/top-headlines/sources?apiKey=dd0594311c8644e7a83119ce2dcdd00b&language=fr&country=fr"
    )
      .then((response) => response.json())
      .then((data) =>
        setSourceList(
          data.sources.map((element) => ({
            id: element.id,
            name: element.name,
            url: element.url,
            description: element.description,
            category: element.category,
          }))
        )
      );
  }, []);

  return (
    <div>
      <Nav />

      <div className="Banner">
        <Button onClick={() => changeLangue("fr")}>French</Button>
        <Button onClick={() => changeLangue("en")}>English</Button>
      </div>
      <div className="HomeThemes">
        <List
          itemLayout="horizontal"
          dataSource={sourceList}
          renderItem={(item) => (
            <List.Item>
              <List.Item.Meta
                avatar={
                  <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
                }
                title={
                  <Link to={"/screenMyArticlesBySource/" + item.id}>
                    {item.name}
                  </Link>
                }
                description={item.description}
              />
            </List.Item>
          )}
        />
      </div>
    </div>
  );
}

export default ScreenSource;
