import React from "react";
import "./App.css";
import { Card, Icon, Row, Col, Modal } from "antd";
import Nav from "./Nav";
import { useParams } from "react-router-dom";
import { useState } from "react";
import { useEffect } from "react";
const { Meta } = Card;

function ScreenArticlesBySource() {
  var { id } = useParams();

  console.log("ID :", typeof id);

  const [articleArray, setArticleArray] = useState([]);
  const [modalArticle, setModalArticle] = useState();
  const [modalTitle, setModalTitle] = useState();
  const [modalContent, setModalContent] = useState();

  const [isModalVisible, setIsModalVisible] = useState(false);

  const showModal = (i) => {
    setModalTitle(articleArray[i].title);
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  useEffect(() => {
    var req =
      "https://newsapi.org/v2/top-headlines?apiKey=dd0594311c8644e7a83119ce2dcdd00b&language=fr&sources=" +
      id;
    fetch(req)
      .then((response) => response.json())
      .then((data) => {
        setArticleArray(data.articles);
        setModalArticle(data.articles[0]);
      });
  }, []);

  return (
    <div>
      <Nav />
      <div className="Banner" />
      <div className="site-card-wrapper">
        <Row gutter={16}>
          {articleArray.map((element, i) => (
            <Col span={8}>
              <Card
                cover={<img alt="example" src={element.urlToImage} />}
                actions={[
                  <Icon
                    type="read"
                    key="ellipsis2"
                    onClick={() => showModal(i)}
                  />,
                  <Icon type="like" key="ellipsis" />,
                ]}
              >
                <Meta title={element.title} description={element.description} />
              </Card>
            </Col>
          ))}
        </Row>
      </div>
      if(modalArticle)
      {
        <Modal
          title="{modalArticle.title}"
          visible={isModalVisible}
          onOk={handleOk}
          onCancel={handleCancel}
        >
          <p>{modalArticle.content}</p>
        </Modal>
      }
    </div>
  );
}

export default ScreenArticlesBySource;
