import React from 'react';
import './App.css';
import {Input,Button} from 'antd';
import {Redirect} from 'react-router-dom';
import {useState} from 'react'

function ScreenHome() {

  const [isLogged, setIsLogged] = useState(false);

  function handleClick() 
  {
    setIsLogged(!isLogged);
  }

  if(isLogged)
  {
    return (<Redirect to='/screenMyArticles'/>);
  }

  return (


    <div className="Login-page" >

          {/* SIGN-IN */}

          <div className="Sign">

                  <Input className="Login-input" placeholder="arthur@lacapsule.com" />

                  <Input.Password className="Login-input" placeholder="password" />


            <Button style={{width:'80px'}} type="primary" onClick={() => handleClick()Â }>Sign-in</Button>

          </div>

          {/* SIGN-UP */}

          <div className="Sign">

                  <Input className="Login-input" placeholder="Arthur G" />

                  <Input.Password className="Login-input" placeholder="password" />


            <Button style={{width:'80px'}} type="primary">Sign-up</Button>

          </div>

      </div>
  );
}

export default ScreenHome;
