import React from "react";
import "./App.css";
import { Card, Col, Icon, Row } from "antd";
import Nav from "./Nav";
import { connect } from "react-redux";

const { Meta } = Card;

function ScreenMyArticles(props) {
  var noArticles;
  if (props.articleList.length === 0) {
    noArticles = "No articles";
  }
  return (
    <div>
      <Nav />
      <div className="Banner" />
      {noArticles}
      <div className="Card">
        <div style={{ display: "flex", justifyContent: "center" }}>
          <Row gutter={16}>
            {props.articleList.map((element, i) => (
              <Card
                style={{
                  width: 300,
                  margin: "15px",
                  display: "flex",
                  flexDirection: "column",
                  justifyContent: "space-between",
                }}
                cover={<img alt="example" src={element.img} />}
                actions={[
                  <Icon type="read" key="ellipsis2" />,
                  <Icon
                    type="delete"
                    key="ellipsis"
                    onClick={() => props.removeArticle(element.title)}
                  />,
                ]}
              >
                <Meta title={element.title} description={element.description} />
              </Card>
            ))}
          </Row>
        </div>
      </div>
    </div>
  );
}

function mapStateToProps(state) {
  return { articleList: state.articleList };
}

function mapDispatchToProps(dispatch) {
  return {
    removeArticle: function (articleTitle) {
      dispatch({ type: "removeArticle", articleTitle });
    },
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(ScreenMyArticles);
