import React from "react";
import "./App.css";
import { Card, Icon, Row, Col, Modal, Button } from "antd";
import Nav from "./Nav";
import { useParams } from "react-router-dom";
import { useState } from "react";
import { useEffect } from "react";
import { connect } from "react-redux";
import userController from "../../controller/articleController";
const { Meta } = Card;

function ScreenArticlesBySource(props) {
  var { id } = useParams();

  const [articleArray, setArticleArray] = useState([]);
  const [modalTitle, setModalTitle] = useState("");
  const [modalContent, setModalContent] = useState("");

  const [isModalVisible, setIsModalVisible] = useState(false);

  const showModal = (i) => {
    setModalTitle(articleArray[i].title);
    setModalContent(articleArray[i].content);
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  function addArticle(title, description, img) {
    props.addArticle({
      title: title,
      description: description,
      img: img,
    });
    userController.addArticle(props.token, title, description, img);
  }

  useEffect(() => {
    var req =
      "https://newsapi.org/v2/top-headlines?apiKey=dd0594311c8644e7a83119ce2dcdd00b&language=" +
      props.language +
      "&sources=" +
      id;
    fetch(req)
      .then((response) => response.json())
      .then((data) => {
        setArticleArray(data.articles);
      });
  }, []);

  useEffect(() => {
    var req =
      "https://newsapi.org/v2/top-headlines?apiKey=dd0594311c8644e7a83119ce2dcdd00b&language=" +
      props.language +
      "&sources=" +
      id;
    fetch(req)
      .then((response) => response.json())
      .then((data) => {
        setArticleArray(data.articles);
      });
  }, [props.language]);

  return (
    <div>
      <Nav />
      <div className="Banner"></div>
      <div style={{ display: "flex", justifyContent: "center" }}>
        <Row gutter={16} type="flex">
          {articleArray.map((element, i) => (
            <Col xs={24} lg={8}>
              <Card
                style={{
                  width: 300,
                  height: 500,

                  margin: "15px",
                  display: "flex",
                  flexDirection: "column",
                  justifyContent: "space-between",
                }}
                cover={<img alt="example" src={element.urlToImage} />}
                actions={[
                  <Icon
                    type="read"
                    key="ellipsis2"
                    onClick={() => showModal(i)}
                  />,
                  <Icon
                    type="like"
                    key="ellipsis"
                    onClick={() =>
                      addArticle(
                        element.title,
                        element.description,
                        element.img
                      )
                    }
                  />,
                ]}
              >
                <Meta title={element.title} description={element.description} />
              </Card>
            </Col>
          ))}
        </Row>
      </div>
      <Modal
        title={modalTitle}
        visible={isModalVisible}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <p>{modalContent}</p>
      </Modal>
    </div>
  );
}

function mapStateToProps(state) {
  return { language: state.language };
}

function mapDispatchToProps(dispatch) {
  return {
    addArticle: function (article) {
      dispatch({ type: "addArticle", article });
    },
    changeLanguage: function (langue) {
      dispatch({ type: "changeLanguage", langue });
    },
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ScreenArticlesBySource);
